mapper.select:
SELECT a.id, a.office_id AS "office.id", a. NAME, a.enname, a.role_type AS roleType, a.data_scope AS dataScope, a.remark, a.create_user AS "createBy.id", a.create_date createDate, a.update_user AS "updateBy.id", a.update_date updateData, a.del_flag AS "delFlag", o. NAME AS "office.name", o. CODE, a.useable AS useable , ro.office_id AS "officeList.id" FROM sys_role a LEFT JOIN sys_office o ON o.id = a.office_id LEFT JOIN sys_user_role ur ON ur.sys_role_id = a.id LEFT JOIN sys_user u ON u.id = ur.sys_user_id LEFT JOIN sys_role_office ro ON ro.role_id = a.id WHERE a.del_flag = "0" AND a.useable= "1"
AND u.id = #{user.id}
AND u.login_name = #{user.loginName}
${sqlMap.dsf} ORDER BY o.code, a.name

SELECT r.id, r.name, r.enname, r.create_user AS createUser, DATE_FORMAT(r.create_date,'%Y-%m-%d %H:%i:%s') as createDate FROM sys_role r where 1=1
and r.name = #{role.name}
order by r.create_date desc
